package kap02_Thread_Konzept;/** * Codebeispiel zur Erzeugung von Threads mithilfe des Interface Runnable */class MyOtherWorker implements Runnable{  @Override  public void run()  {    Thread self = Thread.currentThread();    System.out.println("Hallo Welt von " + self.getName());    System.out.println("Die ID von " + self.getName() + " ist " + self.getId());  }}public class ThreadDurchInterface{  public static void main(String[] args)  {    for (int i = 0; i < 3; i++)    {      Thread t = new Thread(new MyOtherWorker(), "Worker " + i);      t.start();    }  }}