package kap02_Thread_Konzept;/** * Codebeispiel zur Erzeugung von Threads durch Vererbung. */class MyWorker extends Thread{  public MyWorker(String name)  {    super(name);  }  @Override  public void run()  {    Thread self = Thread.currentThread();    System.out.println("Hallo Welt von " + self.getName());    System.out.println("Die ID von " + self.getName() + " ist " + self.getId());        while (true){    	    }      }}public class ThreadDurchVererbung{  public static void main(String[] args)  {	  Thread self = Thread.currentThread();	  self.setName("HAHA");	  System.out.println("Die ID von " + self.getName() + " ist " + self.getId());	  for (int i = 0; i < 3; i++)    {      MyWorker t = new MyWorker("Worker " + i);      t.start();    }          }}